mapper.update:
update banner
set name = #{name,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR},
  delete_time = #{deleteTime,jdbcType=INTEGER},
  update_time = #{updateTime,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, description, delete_time, update_time
from banner
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into banner (banner_id, img, link, 
  valid_status, c_time, u_time
  )
values (#{bannerId,jdbcType=BIGINT}, #{img,jdbcType=VARCHAR}, #{link,jdbcType=VARCHAR}, 
  #{validStatus,jdbcType=BIT}, #{cTime,jdbcType=TIMESTAMP}, #{uTime,jdbcType=TIMESTAMP}
  )
